import { Socket } from "phoenix";
import { goto } from "@sapper/app";

let socket;
let onboardingChannel;
let me;

export async function getSocket() {
  return socket || (await createSocket());
}

export function getMe() {
  return me;
}

async function init() {
  const res = await fetch("/api/me", { credentials: "include" });

  if (res.ok) {
    const json = await res.json();

    me = json.me;
    const { next, token } = json;

    if (next === "blocked") {
      goto("/blocked");
      return;
    } else if (next === "onboarding") {
      // if (!location.href.includes("onboarding")) {
      //   goto("/onboarding");
      // }
    }

    socket = new Socket("/api/socket", {
      params: { token },
      logger: (kind, msg, data) => {
        console.log(`${kind}: ${msg}`, data);
      },
    });

    socket.onClose(() => {
      // TODO set error (show disconnected banner)
    });

    socket.connect();
  } else if (res.status === 401) {
    goto("/auth");
  } else {
    // TODO
    goto("/");
  }
}

async function createSocket() {
  await init();
  return socket;
}

export async function getOnboardingChannel() {
  return onboardingChannel || (await createOnboardingChannel());
}

async function createOnboardingChannel() {
  const socket = await getSocket();
  onboardingChannel = socket.channel(`onboarding:${me}`);
  onboardingChannel.join();
  return onboardingChannel;
}

async function fetchPlus(path, options = {}) {
  if (navigator.onLine) return fetch(path, options);
  alert(`Интернета нету :(`);
  return { offline: true };
}

export async function gatherMeta() {
  return {
    platform: navigator.platform,
    userAgent: navigator.userAgent,
    appVersion: navigator.appVersion,
    vendor: navigator.vendor,
    opera: window.opera,
    screen: [window.screen.availWidth, window.screen.availHeight],
    devicePixelRatio: window.devicePixelRatio,
  };
}

export async function visited(id, code) {
  return await fetchPlus("/api/visited", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ id, meta: { code, ...gatherMeta() } }),
  });
}

export async function isCodeAvailalbe(code) {
  const res = await fetchPlus(
    "/api/is-code-available/" + encodeURIComponent(code)
  );
  const text = await res.text();
  return text === "true";
}

export async function savePhone({ phoneNumber, visit_id, referral_code }) {
  return await fetchPlus("/api/share-phone", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      phone: phoneNumber,
      meta: {
        visit: visit_id,
        code: referral_code,
        ...gatherMeta(),
      },
    }),
  });
}

export async function saveCode(code, visitId, referral_code) {
  return await fetchPlus("/api/save-code", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      code,
      meta: {
        visit: visitId,
        code: referral_code,
      },
    }),
  });
}

